INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("SharePoint 2010 Site Owner''s Manual", 'Yvonne M. Harryman', 'SharePoint 2010 Site Owner''s Manual starts by assuming you already have SharePoint installed on your system and are looking for ways to solve the problems you face every day in your organization. You''ll learn to determine what type of SharePoint installation you have Microsoft Office SharePoint Server (MOSS), Windows SharePoint Services (WSS), the "Fabulous 40" templates and what features are at your disposal. Once you know the lay of the land, you''ll discover what you can do yourself, when you need to call in some help, and when you should leave it to the developers.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/harryman.jpg', NULL, 0, NULL);
INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("Java Development with Ant", 'Erik Hatcher, Steve Loughran', 'The most widely used build tool for Java projects, Ant is cross-platform, extensible, simple, and fast. It scales from small personal projects to large, multi-team J2EE projects. And, most important, it''s easy to learn.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/hatcher.jpg', NULL, 0, NULL);
