INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("Lucene in Action, Second Edition", 'Erik Hatcher, Otis Gospodnetic, , Michael McCandless', 'With clear writing, reusable examples, and unmatched advice on best practices, Lucene in Action is still the definitive guide to developing with Lucene.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/hatcher3.jpg', NULL, 0, NULL);
INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("PowerBuilder 6.0 Questions & Answers", 'Tim Hatton', 'If you''d like to learn PowerBuilder--or enhance your skills-- this book is for you. Its hands-on approach will show you how to write real code. Each section takes a specific "How do I " topic and answers commonly asked questions in an easy-to-understand, conversational manner. It then shows you how the same technique can be used over and over again to decrease your overall code-writing time.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/hatton.jpg', NULL, 0, NULL);
INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("The Awesome Power of PowerJ", 'Tim Hatton', 'The Awesome Power of PowerJ shows you how you can write Java programs the very first day with PowerJ, even if you don''t know Java. Through a hands-on approach that makes liberal use of figures and code snippets, you will learn how to use PowerJ to build effective Java applets and applications.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/hatton2.jpg', NULL, 0, NULL);
INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("The Awesome Power of Power++", 'Tim Hatton', 'The Awesome Power of Power++ is for the beginning to intermediate Power++ programmer. It assumes that you have little or no knowledge of the C++ language but that you do know programming constructs. The purpose is to teach you how to use Power++ to build C++ applets and applications even if you are not a C++ expert. To this end it takes a hands-on approach and makes liberal use of figures and code snippets.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/hatton3.jpg', NULL, 0, NULL);
INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("Azure in Action", 'Chris Hay, Brian H. Prince', 'Azure in Action is a fast-paced tutorial intended for architects and developers looking to develop on Windows Azure and the Windows Azure Platform. It''s designed both for readers new to cloud concepts and for those familiar with cloud development but new to Azure. After a quick walk through the basics, it guides you all the way from your first app through more advanced concepts of the Windows Azure Platform.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/hay.jpg', NULL, 0, NULL);
INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("Distributed Agile in Action", 'Vikas Hazrati, Balaji D Loganathan', 'Distributed Agile in Action is the first book to directly address the unique task of going Agile in a distributed team. Rather than rehashing Agile theories, this book supplies the practical examples and step by step advice you need to help your distributed teams adopt and embrace Agile principles. It''s a distilled and carefully organized learning aid for working in a distributed Agile environment, with in-depth focus on how to approach three critical components of development-People, Process and Technology.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/hazrati.jpg', NULL, 0, NULL);
