INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("Camel in Action", 'Claus Ibsen, Jonathan Anstey', 'Camel in Action is for developers working with integration of any kind. This highly practical book introduces Camel and shows examples of how to use it with the 45+ supported enterprise integration patterns. Written by the people who wrote the Camel code, it''s up to date and distills details and insights that only people deeply involved with Camel could provide.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/ibsen.jpg', NULL, 0, NULL);
INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("Taming Text", 'Grant S. Ingersoll, Thomas S. Morton, , Andrew L. Farris', 'Taming Text is a hands-on, example-driven guide to working with unstructured text in the context of real-world applications. This book explores how to automatically organize text using approaches such as full-text search, proper name recognition, clustering, tagging, information extraction, and summarization. The book guides you through examples illustrating each of these topics, as well as the foundations upon which they are built.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/ingersoll.jpg', NULL, 0, NULL);
