INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("Tuscany SCA in Action", 'Simon Laws, Mark Combellack, Raymond Feng, Haleh Mahbod, Simon Nash', 'Tuscany in Action is a comprehensive, hands-on guide for developing technology agnostic, extensible applications using Apache Tuscany''s lightweight SCA infrastructure. The book uses practical examples based on a travel booking scenario to demonstrate how to develop applications with Tuscany SCA. Apache Tuscany supports a variety of programming environments, data bindings and communication protocols "out of the box" and can be easily extended to support other technologies.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/laws.jpg', NULL, 0, NULL);
