INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("Erlang and OTP in Action", 'Martin Logan, Eric Merritt, , Richard Carlsson', 'Erlang and OTP in Action teaches you to apply Erlang''s shared-state model for concurrent programming--a completely different way of tackling the problem of parallel programming from the more common multi-threaded approach. This book walks you through the practical considerations and steps of building systems in Erlang and integrating them with real-world C/C++, Java, and .NET applications. Unlike other books on the market, Erlang and OTP in Action offers a comprehensive view of how concurrency relates to SOA and web technologies.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/logan.jpg', NULL, 0, NULL);
INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("SharePoint 2007 Developer''s Guide to Business Data Catalog", 'Brett Lonsdale, Nick Swan', 'SharePoint 2007 Developer''s Guide to Business Data Catalog is a practical, example-rich guide to the features of the BDC and the techniques you need to build solutions for end users. The book starts with the basics what the BDC is, what you can do with it, and how to pull together a BDC solution. With the fundamentals in hand, it explores the techniques and ideas you need to put BDC into use effectively in your organization.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/lonsdale.jpg', NULL, 0, NULL);
