INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("Advanced OWL 5.0", 'Ted Neward', 'Advanced OWL 5.0 covers advanced ways to make the OWL library do those tricky things you thought weren''t possible. You''ll get a long, exhaustive look at the new features introduced by the OWL 5.0 code. You''ll find detailed explanations of how to extend the OWL objects themselves in new directions.', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/neward2.jpg', NULL, 0, NULL);
