INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("SharePoint 2010 Workflows in Action", 'Phil Wicklund', 'SharePoint 2010 Workflows in Action is a hands-on guide for workflow application development in SharePoint. Power users are introduced to the simplicity of building and integrating workflows using SharePoint Designer, Visio, InfoPath, and Office. Developers will learn to build custom processes and use external data sources. They will learn about state machine workflows, ASP.NET forms, event handlers, and much more. This book requires no previous experience with workflow app development. ', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/wicklund.jpg', NULL, 0, NULL);
INSERT INTO items (primaryTitle, secondaryTitle, description, thumbnailUrl, takenByUserId, accessoryTaken, timeTaken) VALUES ("SharePoint 2010 Web Parts in Action", 'Wictor Wilén', 'SharePoint 2010 Web Parts in Action is a comprehensive guide to deploying, customizing, and creating Web Parts. Countless examples walk you through everything from design, to development, deployment, troubleshooting, and upgrading. Because Web Parts are ASP.NET controls, you''ll learn to use Visual Studio 2010 to extend existing Web Parts and to build custom components from scratch. ', 'https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/wilen.jpg', NULL, 0, NULL);
